frappe.ui.form.on('Delivery Note Item', {
    custom_delivered_qty: function(frm, cdt, cdn) {
        update_delivered_qty_amount(frm, cdt, cdn);
    },
    rate: function(frm, cdt, cdn) {
        update_delivered_qty_amount(frm, cdt, cdn);
    }
});

function update_delivered_qty_amount(frm, cdt, cdn) {
    let row = locals[cdt][cdn];
    if (row.custom_delivered_qty && row.rate) {
        row.custom_delivered_qty_amount = row.custom_delivered_qty * row.rate;
        frm.refresh_field("items");
    }
}

