frappe.ui.form.on('Sales Invoice', {
    refresh: function(frm) {
        frm.add_custom_button('Generate', function() {
            frappe.call({
                method: "oryx.si.generate_sales_invoice",
                args: {
                    sales_invoice_name: frm.doc.name
                },
                callback: function(r) {
                    if (!r.exc) {
                        frappe.msgprint(__('Sales Invoice successfully created in Site B.'));
                        // Optionally, update the custom_reference_code field in the UI
                        frm.set_value('custom_reference_code', r.message.naming_series);
                    }
                }
            });
        });
    }
});

frappe.ui.form.on('Customer', {
    refresh: function (frm) {
        frm.add_custom_button('Generate', function () {
            frappe.confirm(
                __('Are you sure you want to create this customer in Site B?'),
                function () {
                    frappe.call({
                        method: "oryx.customer.generate_customer",
                        args: {
                            customer_name: frm.doc.name
                        },
                        callback: function (r) {
                            if (!r.exc) {
                                frappe.msgprint(__('Customer successfully created in Site B.'));
                            } else {
                                frappe.msgprint(__('Failed to create customer in Site B.'));
                            }
                        }
                    });
                }
            );
        });
    }
});

frappe.ui.form.on('PO Form', {
    refresh: function(frm) {
        if (!frm.is_new()) {
            frm.add_custom_button(__('Create Purchase Order'), function() {
                create_purchase_order(frm);
            }, __('Actions'));
        }
    }
});

function create_purchase_order(frm) {
    frappe.model.with_doctype('Purchase Order', function() {
        let po = frappe.model.get_new_doc('Purchase Order');
        po.transaction_date = frm.doc.date;
        po.supplier = frm.doc.supplier;
        po.custom_po_form = frm.doc.name;

        frm.doc.items.forEach(row => {
            let item = frappe.model.add_child(po, 'items');
            item.item_code = row.item_code;
            item.item_name = row.item_name;
            item.uom = row.uom;
            item.stock_uom = row.uom;
            item.qty = row.qty;
            item.schedule_date = frm.doc.date; // Default schedule date to PO Form date
        });

        frappe.set_route('Form', 'Purchase Order', po.name);
    });
}

