function calculate_net_weight(frm, cdt, cdn) {
    let row = locals[cdt][cdn];
    let before_loading = row.before_loading || 0;
    let after_loading = row.after_loading || 0;

    frappe.model.set_value(cdt, cdn, 'net_weight', after_loading - before_loading);

    calculate_total_net_weight(frm);  // Update total net weight
}

