function create_purchase_order(frm) {
    frappe.model.with_doctype('Purchase Order', function() {
        let po = frappe.model.get_new_doc('Purchase Order');
        po.transaction_date = frm.doc.date;
        po.supplier = frm.doc.supplier;
        po.custom_po_form = frm.doc.name;

        let promises = frm.doc.item_details.map(row => {
            return new Promise(resolve => {
                frappe.db.get_value('Item', row.item_code, 'stock_uom').then(response => {
                    let stock_uom = response.message.stock_uom || row.uom;
                    let item = frappe.model.add_child(po, 'items');
                    item.item_code = row.item_code;
                    item.item_name = row.item_name;
                    item.uom = row.uom;
                    item.stock_uom = stock_uom;
                    item.qty = row.qty;
                    item.schedule_date = frm.doc.date;
                    item.rate = row.rate;  
                    resolve();
                });
            });
        });

        Promise.all(promises).then(() => {
            frappe.set_route('Form', 'Purchase Order', po.name).then(() => {
                setTimeout(() => {
                    let po_doc = cur_frm.doc;
                    po_doc.items.forEach((item, index) => {
                        item.rate = frm.doc.item_details[index].rate;
                    });
                    cur_frm.refresh_field('items');
                }, 500);
            });
        });
    });
}

function create_delivery_order(frm) {
    frappe.model.with_doctype('DO Form', function() {
        let dn = frappe.model.get_new_doc('DO Form');
        dn.date = frm.doc.date;
        dn.po_form = frm.doc.name;
        dn.with_vat = frm.doc.with_vat;

        let promises = frm.doc.item_details.map(row => {
            return new Promise(resolve => {
                frappe.db.get_value('Item', row.item_code, 'stock_uom').then(response => {
                    let stock_uom = response.message.stock_uom || row.uom;
                    let item = frappe.model.add_child(dn, 'item_details');
                    item.item_code = row.item_code;
                    item.item_name = row.item_name;
                    item.uom = row.uom;
                    item.stock_uom = stock_uom;
                    item.qty = row.qty;
                    item.schedule_date = frm.doc.date;
                    resolve();
                });
            });
        });

        Promise.all(promises).then(() => {
            frm.doc.vehicle_details.forEach(row => {
                let vehicle = frappe.model.add_child(dn, 'vehicle_details');
                vehicle.vehicle = row.vehicle;
                vehicle.driver = row.driver;
                vehicle.warehouse = row.warehouse;
                vehicle.loaded_qty = row.loaded_qty;
                vehicle.meter_reading = row.meter_reading;
                vehicle.before_loading = row.before_loading;
                vehicle.after_loading = row.after_loading;
                vehicle.net_weight = row.net_weight;
            });

            frappe.set_route('Form', 'DO Form', dn.name);
        });
    });
}


