frappe.ui.form.on('Purchase Receipt', {
    before_save: function(frm) {  // Trigger before saving the record
        if (frm.doc.custom_po_form) {
            fetch_vehicle_details(frm);
        }
    }
});

function fetch_vehicle_details(frm) {
    frappe.call({
        method: 'frappe.client.get',
        args: {
            doctype: 'PO Form',  // Replace with your actual custom Doctype name
            name: frm.doc.custom_po_form
        },
        async: false,  // Ensures the data is fetched before saving
        callback: function(response) {
            if (response.message) {
                let po_data = response.message;

                // Clear existing child table entries to prevent duplicates
                frm.clear_table('custom_vehicle_details');

                // Loop through vehicle_details from PO Form and add to Purchase Receipt
                (po_data.vehicle_details || []).forEach(row => {
                    let new_row = frm.add_child('custom_vehicle_details');
                    new_row.vehicle = row.vehicle;
                    new_row.driver = row.driver;
                    new_row.warehouse = row.warehouse;
                    new_row.loaded_qty = row.loaded_qty;
                    new_row.meter_reading = row.meter_reading;
                    new_row.before_loading = row.before_loading;
                    new_row.after_loading = row.after_loading;
                    new_row.net_weight = row.net_weight;
                });

                frm.refresh_field('custom_vehicle_details');
            }
        }
    });
}

