        so.custom_ship = frm.doc.ship_name;
        so.custom_berth_no = frm.doc.berth_no;
        so.delivery_date = frm.doc.delivery_date;
        so.custom_mode_of_supply = frm.doc.mode_of_supply;
        so.custom_arrange_surveyor = frm.doc.arrange_surveyor;
        frm.doc.item_details.forEach(row => {
            let item = frappe.model.add_child(so, 'items');
            item.item_code = row.item_code;
            item.item_name = row.item_name;
            item.uom = row.uom;
            item.qty = row.qty;
            item.rate = row.rate;
            item.schedule_date = frm.doc.date; // Default schedule date to PO Form date
        });
        frappe.set_route('Form', 'Sales Order', so.name);
    });
}

