frappe.ui.form.on('Delivery Note Item', {
    custom_meter_reading_before: function(frm, cdt, cdn) {
        calculate_loading_weights(frm, cdt, cdn);
    },
    custom_meter_reading_after: function(frm, cdt, cdn) {
        calculate_loading_weights(frm, cdt, cdn);
    },
    custom_default_item_weight: function(frm, cdt, cdn) {
        calculate_loading_weights(frm, cdt, cdn);
    },
    custom_default_vehicle_weight: function(frm, cdt, cdn) {
        calculate_loading_weights(frm, cdt, cdn);
    }
});

function calculate_loading_weights(frm, cdt, cdn) {
    let item = locals[cdt][cdn];

    let meter_reading_before = flt(item.custom_meter_reading_before);
    let meter_reading_after = flt(item.custom_meter_reading_after);
    let item_weight = flt(item.custom_default_item_weight);
    let vehicle_weight = flt(item.custom_default_vehicle_weight);

    // Calculate Before Loading Weight
    item.custom_before_loading_weight = (meter_reading_before * item_weight) + vehicle_weight;

    // Calculate After Loading Weight
    item.custom_after_loading_weight = (meter_reading_after * item_weight) + vehicle_weight;

    frm.refresh_field('items');
}

frappe.ui.form.on('Goods Receive Trip', {
    meter_readingbl: function(frm) {
        calculate_loading_weights(frm);
    },
    meter_readingal: function(frm) {
        calculate_loading_weights(frm);
    },
    default_vehicle_weight: function(frm) {
        calculate_loading_weights(frm);
    },
    default_item_weight: function(frm) {
        calculate_loading_weights(frm);
    }
});

function calculate_loading_weights(frm) {
    let meter_readingbl = flt(frm.doc.meter_readingbl);
    let meter_readingal = flt(frm.doc.meter_readingal);
    let item_weight = flt(frm.doc.default_item_weight);
    let vehicle_weight = flt(frm.doc.default_vehicle_weight);

    // Calculate Before Loading Weight
    let weightbl = (meter_readingbl * item_weight) + vehicle_weight;
    frm.set_value("weightbl", weightbl);

    // Calculate After Loading Weight
    let weightal = (meter_readingal * item_weight) + vehicle_weight;
    frm.set_value("weightal", weightal);

    // Calculate Net Weight
    let net_weight = weightal - weightbl;
    frm.set_value("net_weight", net_weight);
}

