function calculate_net_weight(frm, cdt, cdn) {
    let row = locals[cdt][cdn];  // Get the child table row

    let before_loading = flt(row.before_loading) || 0; // Ensure number
    let after_loading = flt(row.after_loading) || 0;

    let net_weight = after_loading - before_loading;

    frappe.model.set_value(cdt, cdn, 'net_weight', net_weight);  // Set net weight

    calculate_total_net_weight(frm); // Update total net weight in parent form
}

