frappe.ui.form.on('Delivery Note', {
    before_save: function(frm) {
        if (frm.doc.custom_do_form) {
            fetch_vehicle_details(frm);
        }
    }
});

function fetch_vehicle_details(frm) {
    frappe.call({
        method: 'frappe.client.get',
        args: {
            doctype: 'DO Form',
            name: frm.doc.custom_do_form
        },
        callback: function(response) {
            if (response.message) {
                let do_data = response.message;
                frm.clear_table('items');

                (do_data.vehicle_details || []).forEach(vehicle_row => {
                    (do_data.item_details || []).forEach(item_row => {
                        let new_row = frm.add_child('items');

                        // Vehicle Info
                        new_row.custom_vehicle = vehicle_row.vehicle;
                        new_row.custom_driver = vehicle_row.driver;
                        new_row.warehouse = vehicle_row.warehouse;
                        new_row.custom_meter_reading_before = vehicle_row.meter_reading;
                        new_row.custom_before_loading_weight = vehicle_row.before_loading;
                        new_row.custom_meter_reading_after = vehicle_row.meter_reading_after;
                        new_row.custom_after_loading_weight = vehicle_row.after_loading;
                        new_row.loading_point = vehicle_row.loading_point;
                        new_row.address_display = vehicle_row.address_display;

                        // Item Info
                        new_row.item_code = item_row.item_code;
                        new_row.item_name = item_row.item_name;
                        new_row.uom = item_row.uom;
                        new_row.rate = item_row.rate;
                        new_row.amount = item_row.amount;
                        new_row.qty = vehicle_row.loaded_qty;  // Or item_row.qty if preferred
                        new_row.custom_loading_uom = item_row.uom;

                        // Stock Info
                        new_row.stock_uom = item_row.stock_uom || "";
                        new_row.conversion_factor = item_row.conversion_factor || 1;

                        // Fallback: Fetch from Item master
                        if (!new_row.stock_uom || !new_row.conversion_factor) {
                            frappe.call({
                                method: 'frappe.client.get',
                                args: {
                                    doctype: 'Item',
                                    name: item_row.item_code
                                },
                                callback: function(item_response) {
                                    if (item_response.message) {
                                        frappe.model.set_value(new_row.doctype, new_row.name, 'stock_uom', item_response.message.stock_uom);
                                        frappe.model.set_value(new_row.doctype, new_row.name, 'conversion_factor', 1);
                                    }
                                }
                            });
                        }
                    });
                });

                frm.refresh_field('items');
            } else {
                frappe.msgprint(__('DO Form not found: ') + frm.doc.custom_do_form);
            }
        }
    });
}

