    weightbl: function(frm, cdt, cdn) {
        calculate_net_weight(frm, cdt, cdn);
    },
    weightal: function(frm, cdt, cdn) {
        calculate_net_weight(frm, cdt, cdn);
    },
    net_weight: function(frm, cdt, cdn) {
        calculate_total_net_weight(frm);
    }
});

function calculate_net_weight(frm, cdt, cdn) {
    let row = locals[cdt][cdn];  // Get the child table row

    let weightbl = flt(row.weightbl) || 0; // Ensure number
    let weightal = flt(row.weightal) || 0;

    let net_weight = weightal - weightbl;

    frappe.model.set_value(cdt, cdn, 'net_weight', net_weight);  // Set net weight

    calculate_total_net_weight(frm); // Update total net weight in parent form
}

