function update_vehicle_uom(frm) {
    if (!frm.doc.item_details || frm.doc.item_details.length === 0) {
        return;
    }

    // Get UOM from the first row in item_details
    let first_item_row = frm.doc.item_details[0];

    if (first_item_row.uom) {
        (frm.doc.vehicle_details || []).forEach(row => {
            frappe.model.set_value(row.doctype, row.name, 'uom', first_item_row.uom);
        });

        // Ensure field refresh so UI updates without needing manual refresh
        frm.refresh_field('vehicle_details');
    }
}

