    // Get Loading Point from the PO Form (Main Form)
    let loading_point = frm.doc.loading_point || "";

    // Update each row in vehicle_details
    frm.doc.vehicle_details.forEach(row => {
        frappe.model.set_value(row.doctype, row.name, 'loading_point', loading_point);
    });

    // Refresh the field to reflect the update in UI
    frm.refresh_field('vehicle_details');
}

