function calculate_imperial_gallon(cdt, cdn) {
    let row = locals[cdt][cdn];

    if (row.qty && row.weight_bridge_wt) {
        let imperial_gallon = row.qty / row.weight_bridge_wt;
        frappe.model.set_value(cdt, cdn, 'imperial_gallon', imperial_gallon);

        // Calculate one_imperial_gallon
        let one_imperial_gallon = imperial_gallon / row.weight_bridge_wt;
        frappe.model.set_value(cdt, cdn, 'one_imperial_gallon', one_imperial_gallon);
    } else {
        frappe.model.set_value(cdt, cdn, 'imperial_gallon', 0);
        frappe.model.set_value(cdt, cdn, 'one_imperial_gallon', 0);
    }
}

