 frappe.ui.form.on('DO Form', {
    refresh(frm) {
        frm.add_custom_button('Create PO Form', () => {
            // Create new PO Form
            frappe.model.with_doctype('PO Form', function () {
                let po_doc = frappe.model.get_new_doc('PO Form');

                // Set main fields
                po_doc.supplier = frm.doc.supplier || "";
                po_doc.transaction_date = frappe.datetime.nowdate();
                po_doc.schedule_date = frappe.datetime.nowdate();
                po_doc.custom_do_form = frm.doc.name;

                po_doc.loading_date = frm.doc.delivery_date || "";
                po_doc.date = frm.doc.date || "";
                po_doc.loading_time = frm.doc.delivery_time || "";
                po_doc.collection_status = frm.doc.delivery_status || "";
                po_doc.port = frm.doc.port_address || "";

                // Map item_details child table
                (frm.doc.item_details || []).forEach(row => {
                    const child = frappe.model.add_child(po_doc, 'item_details');
                    child.item_code = row.item_code;
                    child.item_name = row.item_name;
                    child.uom = row.uom;
                    child.qty = row.qty;
                    child.rate = row.rate;
                    child.delivery_qty = row.delivery_qty;
                });

                // Map vehicle_details child table
                (frm.doc.vehicle_details || []).forEach(row => {
                    const child = frappe.model.add_child(po_doc, 'vehicle_details');
                    child.vehicle = row.vehicle;
                    child.driver = row.driver;
                    child.driver_name = row.driver_name;
                    child.loaded_qty = row.loaded_qty;
                    child.uom = row.uom,
                    child.meter_reading = row.meter_reading,
                    child.before_loading = row.before_loading,
                    child.meter_readingal = row.meter_readingal,
                    child.after_loading = row.after_loading,
                    child.net_weight = row.net_weight,
                    child.loading_point = frm.doc.port_address || ""; 
                });

                // Navigate to new form
                frappe.set_route('Form', 'PO Form', po_doc.name);
            });
        }, __('Actions'));
    }
});

