function calculate_converted_qty(frm, cdt, cdn) {
    const row = locals[cdt][cdn];
    const qty_mt = flt(row.custom_converted_qty);     // Metric Tons input
    const density = flt(row.custom_density);          // Density in kg/L
    const unit = (row.custom_selling_uom || "").trim();       // Stock UOM: "Liter", "Gallon(UK)", "CBM"

    if (!qty_mt || !unit) {
        frappe.model.set_value(cdt, cdn, "qty", 0);
        return;
    }

    if (!density) {
        frappe.msgprint("Density is required for volume conversions.");
        frappe.model.set_value(cdt, cdn, "qty", 0);
        return;
    }

    // Convert Metric Tons to Liters first
    // const liters = (qty_mt * 1000) / density;
    const liters = (qty_mt*density)/1000;

    let qty_in_stock_uom = 0;
    if (unit === "Litre") {
        qty_in_stock_uom = liters;
    } else if (unit === "Gallon (UK)") {
        // qty_in_stock_uom = qty_mt / 4.546;
        qty_in_stock_uom = (qty_mt * 4.546 * density)/1000 ;
    } else if (unit === "CBM") {
        // qty_in_stock_uom = qty_mt / 1000;
        qty_in_stock_uom = (qty_mt * density) ;
    } else {
        frappe.msgprint(`Unknown UOM: ${unit}`);
        frappe.model.set_value(cdt, cdn, "qty", 0);
        return;
    }

    frappe.model.set_value(cdt, cdn, "qty", qty_in_stock_uom.toFixed(3));
}

