function set_conversion_factor(frm, cdt, cdn) {
    let row = locals[cdt][cdn];

    if (!row.custom_density || !row.uom || !row.stock_uom) return;

    let factor = 1;

    if (row.stock_uom === 'Litre' && row.uom === 'Metric Ton') {
        factor = 1000 / row.custom_density;
    } 
    else if (row.stock_uom === 'Metric Ton' && row.uom === 'Litre') {
        factor = row.custom_density / 1000;
    }

    row.conversion_factor = factor;
    frm.refresh_field("items");
}

