frappe.ui.form.on('Delivery Note', {
    refresh(frm) {
        if (!frm.is_new() && frm.doc.docstatus === 1) {
            frm.add_custom_button('Oryx Delivery Plan', () => {
                frappe.model.with_doctype('Oryx Delivery Plan', () => {
                    // Create a new Oryx Delivery Plan document
                    let doc = frappe.model.get_new_doc('Oryx Delivery Plan');

                    // Set main fields
                    doc.delivery_note = frm.doc.name;
                    doc.customer = frm.doc.customer;
                    doc.company = frm.doc.company;
                    doc.posting_date = frm.doc.posting_date;
                    doc.posting_time = frm.doc.posting_time;

                    // Populate child table items from Delivery Note items
                    (frm.doc.items || []).forEach(item => {
                        let row = frappe.model.add_child(doc, 'Oryx Delivery Plan', 'items');
                        row.item_code = item.item_code;
                        // You can add more fields if needed, e.g.:
                        // row.qty = item.qty;
                        // row.uom = item.uom;
                        // row.description = item.description;
                    });

                    // Open the new document
                    frappe.set_route('Form', 'Oryx Delivery Plan', doc.name);
                });
            }, 'Create');
        }
    }
});
